import React, { useState } from 'react'
import './CreateNote.css'
// const desc = useRef();
import axios from 'axios';

export default function CreateNote(props) {
  const [note,setNote] = useState({
    title:"",
    desc:""
  });

  const handleChange = (event)=>{
    const { name, value } = event.target;
    setNote(prevNote => {
      return {
        ...prevNote,
        [name]: value
      };
    });
  }

  const submitNote = async (event)=>{
    // props.onAdd(note);
    // setNote({
    //     title:"",
    //     desc:""
    // });
    event.preventDefault();
    const { name, value } = event.target;
    const note = {
      userId: "64a6cf351d7943da1297bc0a",
      [name]: value
    };
    try {
      await axios.post("/",note); 
      window.location.reload();
    } catch (err) {
        console.log(err);
    }
  }

  return (
    <div >
      <form action="/">
        <input type="text" 
            name='title'
            onChange={handleChange}
            value={note.title}
            placeholder='Title'
        />
        <textarea type="text" 
            name='desc'
            onChange={handleChange}
            value={note.desc}
            placeholder='Content'
        />
        <button type="submit"
            onClick={submitNote}
        >Add</button>
      </form>
    </div>
  )
}
